<>>v<>>^^<^>><<<v<>>>^v^>v>v<<^^<<^>v<v^>>v^^v^^<<>^v>v><v^>v<^^>^<vv><vv^<>v<><^<<<vv<<v>v<^<<<<^^>v^v^^><<><^^^<v>v^^>>>vvv><>vv<>>^^v^v<<^>v^^v^>vv>^<<v<^<v^>^^<<v<^^>^v^^<^^v<<>>vv<^>>^><><>v>>v<>^<v^^><<>>>"

  case class House(x:Int, y:Int)

  def matchOne(c: Char, current: House): House = c match {
    case '^' => House(current.x, current.y + 1)
    case '>' => House(current.x + 1, current.y)
    case 'v' => House(current.x, current.y - 1)
    case '<' => House(current.x - 1, current.y)
  }

  def parseDirections(directions: String): List[House] = {

    @tailrec def recParse(directions: String, current: House, visited: List[House]): List[House] = {
      if (directions.length > 0) {
        recParse(directions.tail, matchOne(directions.head, current), current :: visited)
      } else {
        current :: visited
      }
    }

    recParse(directions, House(0,0), Nil)
  }

  def countVisitedHouses(houses: List[House]) = houses.groupBy(identity).size

}
